class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        result = 0
        incr = True
        prev = 0

        for n, t in zip(nums, target):
            curr_diff = t - n
            if incr:
                if curr_diff > 0:
                    if curr_diff >= prev:
                        prev = max(prev, curr_diff)
                    else:
                        result += (prev - curr_diff)
                        prev = curr_diff
                else:
                    result += prev
                    incr = not incr
                    prev = abs(curr_diff)
            else:
                if curr_diff < 0:
                    if abs(curr_diff) >= prev:
                        prev = max(prev, abs(curr_diff))
                    else:
                        result += (prev - abs(curr_diff))
                        prev = abs(curr_diff)
                else:
                    result += prev
                    incr = not incr
                    prev = curr_diff
        return result + prev
