class Solution:
    def minimumOperations(self, nums, target) -> int:
        n = len(nums)
        diff = [target[i] - nums[i] for i in range(n)]
        res = abs(diff[0])
        for i in range(1, n):
            if diff[i] > 0 and diff[i - 1] > 0:
                res += diff[i] - diff[i - 1] if diff[i] > diff[i - 1] else 0
            elif diff[i] < 0 and diff[i - 1] < 0:
                res += diff[i - 1] - diff[i] if diff[i - 1] > diff[i] else 0
            else:
                res += abs(diff[i])
        return res
