def for_pos(arr):
    ans = 0
    temp = 0
    for i in arr:
        if i>temp:
            ans += i-temp
        temp = i
    return ans

def break_to(arr):
    ans = []
    inc = True
    for i in arr:
        if not ans:
            ans.append([i])
            continue
        if inc:
            if i>=ans[-1][-1]:
                ans[-1].append(i)
            else:
                inc = False
                ans[-1].append(i)
        else:
            if i<=ans[-1][-1]:
                ans[-1].append(i)
            else:
                ans.append([i])
                inc = True
    return ans

