class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> diff(n);
        for(int i=0;i<n;i++){
          diff[i]=target[i]-nums[i];
        }

        bool f=0;
        int maxi=0;
        long long sum=0;
        for(int i=0;i<n;i++){
          if(!f && diff[i]<0){
            sum+=maxi;
            maxi=abs(diff[i]);
            f=1;
          }
          else if(f && diff[i]>0){
            sum+=maxi;
            maxi=abs(diff[i]);
            f=0;
          }
          else{
            if(maxi>abs(diff[i])){
              sum+=maxi-abs(diff[i]);
              maxi=abs(diff[i]);
            }
            else{

              maxi=max(maxi,abs(diff[i]));
            }
          }
        }

        if(maxi){
          sum+=maxi;
        }
        return sum;
    }
};
