class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ans = 0
        tmp = 0

        for n, t in zip(nums, target):
            diff = n - t
            ad = abs(diff)
            at = abs(tmp)
            if diff * tmp > 0:
                if ad > at:
                    ans += ad - at
            else:
                ans += ad
            tmp = diff

        #ans += abs(tmp)

        return ans
