class Solution {
    int a[100010];
    int sza=0;
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        a[sza++]=target[0]-nums[0];
        for(int i=1;i<n;i++)
        {
            int t=target[i]-nums[i];
            if(t!=a[sza-1]) a[sza++]=t;
        }
        int flag=0;//正数
        long long res=0;
        int b=0;
        for(int i=0;i<sza;i++)
        {
            if(a[i]<0)
            {
                if(flag==0)
                {
                    flag=1;
                    b=abs(a[i]);
                    res+=abs(a[i]);
                }
                else
                {
                    if(b>abs(a[i])) b=abs(a[i]);
                    else 
                    {
                        res+=abs(a[i])-b;
                        b=abs(a[i]);
                    }
                }
            }
            else
            {
                if(flag==1)
                {
                    flag=0;
                    b=abs(a[i]);
                    res+=abs(a[i]);
                }
                else
                {
                    if(b>abs(a[i])) b=abs(a[i]);
                    else 
                    {
                        res+=abs(a[i])-b;
                        b=abs(a[i]);
                    }
                }
            }
        }
        return res;
    }
};
