class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ret = 0;
        long long x = 0;
        for(int i = 0; i < nums.size(); ++i){
            long long d = target[i]-nums[i];
            if(d == 0 || ((x>0)!=(d>0))){
                x = d;
                ret += abs(x);
                continue;
            }

            if(abs(d)>abs(x)){
                ret += (abs(d)-abs(x));
            }
            x = d;
        }
        return ret;
    }
};
