class Solution {
public:
    void print(vector<long long int>&nums){
        for(auto it:nums){
            cout<<it<<" ";
        }
        cout<<endl;
    }
    long long int cost(vector<long long int>&temp){
        long long int ans=temp[0];
        int n=temp.size();
        for(int i=1;i<n;i++){
            ans+=max(temp[i]-temp[i-1],0ll);
        }
        return ans;

    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long>diff;
        int n=nums.size();
        for(int i=0;i<n;i++){
            diff.push_back(-target[i]+nums[i]);
        }
        // print(diff);
        long long int ans=0;
        vector<vector<long long int>>pos;
        for(int i=0;i<n;i++){
            vector<long long int>temp;
            if(diff[i]<=0){

                while(i<n && diff[i]<=0){
                    temp.push_back(-diff[i]);
                    i++;
                }
                i--;
                pos.push_back(temp);

            }
            else{
                while(i<n && diff[i]>0){
                    temp.push_back(diff[i]);
                    i++;
                }
                i--;
                pos.push_back(temp);
            }

        }
        // cout<<pos.size()<<endl;
        for(auto it:pos){
            ans+=cost(it);
        }
        return ans;

    }
};
