class Solution {
public:
    typedef long long ll;
    long long minimumOperations(vector<int>& a, vector<int>& bb) {
        ll p[100005] = {0};
        ll n;
        ll cha[100005] = {0};
        ll l = a.size();
        n = l;
        p[0] = 0;
        for (int i = 1; i <= n; ++i) {
            p[i] = a[i-1]-bb[i-1];
        }
        for (int i = 1; i <= n; ++i) {
            cha[i] += p[i];
            cha[i + 1] -= p[i];
        }

        ll res = 0;
        for (int i = 1; i <= n + 1; ++i) {
            if (cha[i] > 0)
                res += cha[i];
        }
        return res;
    }
};
