class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ops = 0
        diff = [t - n for n,t in zip(nums, target)]
        n = len(diff)

        prev = prevmin = 0
        for i in range(n):
            if diff[i] > prev:
                prev = diff[i]
            elif diff[i] < prev:
                ops += prev - prevmin
                prev = prevmin = max(0, diff[i])
        ops += prev - prevmin

        prev = prevmax = 0
        for i in range(n):
            if diff[i] < prev:
                prev = diff[i]
            elif diff[i] > prev:
                ops += prevmax - prev
                prev = prevmax = min(0, diff[i])
        ops += prevmax - prev

        return ops
