class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        nums = [i-j for i, j in zip(nums, target)]
        prev = []
        self.result = 0
        def helper(nums):
            if len(nums) > 0:
                min_num = min(nums)
                n_nums = []
                self.result += min_num
                for num in nums:
                    if num > min_num:
                        n_nums.append(num-min_num)
                    else:
                        helper(n_nums)
                        n_nums = []
                helper(n_nums)
        is_negative = None
        for num in nums:
            #print(prev)
            if prev == []:
                if num < 0:
                    prev.append(-num)
                    is_negative = True
                else:
                    prev.append(num)
                    is_negative = False
            else:
                if num < 0:
                    if is_negative:
                        prev.append(-num)
                    else:
                        helper(prev)
                        prev = [-num]
                        is_negative = True
                elif num > 0:
                    if is_negative:
                        helper(prev)
                        prev = [num]
                        is_negative = False
                    else:
                        prev.append(num)
                else:
                    helper(prev)
                    prev = []
        helper(prev)
        return self.result
