class Solution {
  public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int p1{}, p2{};
        long long ans{};

        int minID{999999999};
        bool inc{false};
        int s = nums.size();
        priority_queue<int, vector<int>, greater<int>> pq{};
        while (p1 < s) {
            minID = 999999999;

            while (p1 < s && nums[p1] == target[p1])
                p1++;
            while (p2 < s && nums[p2] == target[p2])
                p2++;

            if (p1 >= s) break;
            else if (nums[p1] < target[p1]) inc = true;
            else inc = false;

            while (p2 < s && inc && nums[p2] < target[p2]) {
                minID = min(minID, target[p2] - nums[p2]);
                p2++;
            }
            while (p2 < s && !inc && nums[p2] > target[p2]) {
                minID = min(minID, nums[p2] - target[p2]);
                p2++;
            }

            ans += minID;
            for (int i{p1}; i != p2; i++) {
                if (inc) nums[i] += minID;
                else nums[i] -= minID;
            }

            while (p1 < s && nums[p1] == target[p1])
                p1++;
            p2 = p1;
        }

        return ans;
    }
};
