class Solution {
public:
    bool checkSign(long long a, long long b)
    {
        if(a > 0 && b>0)
            return true;
        if(a<0 && b<0)
            return true;
        return false;
    }
    long long count(vector<long long> &v)
    {
        if(v.size() == 0)
            return 0;
        long long ans = v[0];
        for(int i=1; i<v.size(); i++)
        {
            if(v[i-1] >= v[i])
                ans += 0;
            else
            {
                ans += (0LL+v[i]-v[i-1]);
            }
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<long long> diff(n, 0);
        for(int i=0; i<n; i++)
        {
            diff[i] = target[i]-nums[i];
        }
        int i = 0;
        while(i<n)
        {
            if(diff[i] == 0)
            {
                i++;
                continue;
            }
            vector<long long> v;
            int j = i;
            while(j<n && checkSign(diff[i], diff[j]))
            {
                // cout << diff[j] << " ";
                v.push_back(abs(diff[j]));
                j++;
            }
            // cout << endl;
            ans += count(v);
            i = j;
        }
        return ans;
    }
};
