using ll = long long;
class Solution {
public:
  long long minimumOperations(vector<int>& nums, vector<int>& target) {
    int n = nums.size();
    vector<int> diff(n);
    for(int i = 0; i < n; i++) {
      diff[i] = target[i] - nums[i];
    }
    int left = 0;
    int m = 0;

    int flag = 1;
    ll res = 0;
    for(int i = 0; i < n; i++) {
      if(diff[i] > 0) {
        res += max(0, diff[i] - (i > 0 ? max(0, diff[i - 1]) : 0));
      } else if(diff[i] < 0){
        res += max(0, (i > 0 ? min(0, diff[i - 1]) : 0) - diff[i]);
      }
    }
    return res;
  }
};
