class Solution {
public:

    long long solve(vector<int>& nums,int left,int right) {
        long long ans=0;
        ans=abs(nums[left]);
        for(int i=left+1;i<=right;i++) {
            ans+=max(0,abs(nums[i])-abs(nums[i-1]));
        }
        return ans;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long ans=0;
        int left=0;
        int right=0;
        vector<int> values(n);

        for(int i=0;i<n;i++) {
            values[i]=target[i]-nums[i];
        }

        int i=0;
        while(i<n) {
            if(values[i]>=0) {
                while(i<n and values[i]>=0) {
                    i++;
                }
                ans+=solve(values,left,i-1);
                left=i;
            }
            else {
                while(i<n and values[i]<0) {
                    i++;
                }
                ans+=solve(values,left,i-1);
                left=i;
            }
        }
        return ans;


    }
};
