class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def sign(a):
            if a > 0:
                return 1
            if a < 0:
                return -1
            return 0
        ans = 0
        st = [0]
        for x, y in zip(nums, target):
            diff = x - y
            if sign(diff) != sign(st[-1]):
                ans += abs(st[-1])
                st.clear()
                st.append(diff)
                continue

            if abs(diff) <= abs(st[-1]):
                ans += abs(st[-1] - diff)
                while st and abs(st[-1]) >= abs(diff):
                    st.pop()
            st.append(diff)
        if st:
            ans += abs(st[-1])
        return ans
