class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for(int i = 0; i < nums.size(); i++){
            nums[i] -= target[i];
        }

        long long ans = 0;
        int mini, i = 0, j = 0;
        while(j < nums.size()){
            if(nums[j] > 0){
                i = j;
                while(j < nums.size() && nums[j] > 0) j++;
                mini = nums[i++];
                ans += mini;
                while(i < j){
                    if(mini >= nums[i]) mini = nums[i++];
                    else{
                        ans += nums[i] - mini;
                        mini = nums[i++];
                    }
                }
            }
            else if(nums[j] < 0){
                i = j;
                while(j < nums.size() && nums[j] < 0) j++;
                mini = nums[i++];
                ans -= mini;
                while(i < j){
                    if(mini <= nums[i]) mini = nums[i++];
                    else{
                        ans += -(nums[i] - mini);
                        mini = nums[i++];
                    }
                }
            }
            else j++;
        }
        return ans;
    }
};
