class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long res=0;
        int pre=0;
        transform(begin(nums),end(nums),begin(target),begin(nums),minus<>());
        for(auto& x:nums){
            if((x<0)!=(pre<0)){
                res+=abs(pre);
                pre=0;
            }
            if(abs(x)<abs(pre)){
                res+=abs(pre)-abs(x);
            }
            // printf("%d %d %d;",x,pre,res);

            pre=x;
        }
        return res+abs(pre);
    }
};
