class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size(), dec = 0, inc = 0;
        if(nums[0] < target[0])
            inc += target[0]-nums[0];
        else
            dec += nums[0]-target[0];

        ans += max(inc, dec);

        for(int i = 1; i < n; i++) {
            if(nums[i] < target[i]) {
                int dif = target[i]-nums[i];
                ans += max(dif-inc, 0);
                if(inc > dif) {
                    dec = min(dec, inc-dif);
                    inc = dif+dec;
                } else {
                    inc = dif;
                    dec = 0;
                }
            } else if(nums[i] > target[i]) {
                int dif = nums[i]-target[i];
                ans += max(dif-dec, 0);
                if(dec > dif) {
                    inc = min(inc, dec-dif);
                    dec = dif+inc;
                } else {
                    dec = dif;
                    inc = 0;
                }
            } else {
                inc = min(inc, dec);
                dec = inc;
            }
        }
        return ans;
    }
};
