class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        prev = 0
        total = 0
        for idx in range(len(nums)):
            action = target[idx] - nums[idx]
            if (action > 0) != (prev > 0):
                total += abs(action)
            else:
                if abs(prev) >= abs(action):
                    pass
                else:
                    total += abs(action) - abs(prev)
            prev = action
            print(idx, total)
        return total
