class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();

        vector<int> diff(n);
        for(int i = 0; i < n; i++) {
            diff[i] = target[i] - nums[i];
        }

        long long ret = 0;
        stack<int> st;

        for(int i = 0; i < n; i++) {
            int x = diff[i];

            if(i > 0) {
                int sgn = x == 0 ? 0 : x / abs(x);
                int sgnprev = diff[i - 1] == 0 ? 0 : diff[i - 1] / abs(diff[i - 1]);
                if(sgn != sgnprev) {
                    // std::cout << "here: " << i << "\n";
                    if(st.size()) {
                        ret += abs(st.top());
                        while(st.size()) st.pop();
                    }
                    st.push(x);
                    std::cout << "x: " << x << " " << ret << "\n";

                    continue;
                }
            }

            int ax = abs(x);
            if(!st.size()) {
                st.push(x);
                std::cout << "x: " << x << " " << ret << "\n";

                continue;
            }

            bool flag = 1;

            while(st.size() && abs(x) < abs(st.top())) {
                if(flag) ret += abs(st.top()) - abs(x);
                flag = 0;
                st.pop();
            }

            st.push(x);
            std::cout << "x: " << x << " " << ret << "\n";
        }

        std::cout << ret << " " << st.top();

        return ret + abs(st.top());   
    }
};

