class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        pref = [0]*(n + 1)
        res = 0

        temp = [nums[i] - target[i] for i in range(n)]
        maxi, mini = -1, float('inf')

        f = 0
        for i in temp:
            if i >= 0:
                if f == -1:
                    res += abs(mini)
                    mini = float('inf')
                    f = 1
                    maxi = i
                else:
                    if maxi > i:
                        res += maxi - i
                    maxi = i
                    f = 1
            else:
                if f == 1:
                    res += maxi
                    maxi = -1
                    f = -1
                    mini = i
                else:
                    if mini < i:
                        res += abs(mini - i)
                    mini = i
                    f = -1
            # print(nums, res, maxi, mini)

        if maxi > -1:
            res += maxi
        if mini < float('inf'):
            res += abs(mini)
        return res
