class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long ans=0;
        for(int i=0,j=0;i<n;i=j){
            j=i;
            int ls=-1,mv=1E9;
            while(j<n&&nums[j]>=target[j]){
                if(ls!=-1){
                    ans+=max(0,ls-(nums[j]-target[j]));
                    if(ls>(nums[j]-target[j]))
                        mv=min(mv,(nums[j]-target[j]));
                }
                ls=nums[j]-target[j];
                j++;
            }
            if(j>i)
                mv=nums[j-1]-target[j-1],ans+=mv;

            else{
                ls=-1;
                mv=1E9;
                while(j<n&&nums[j]<target[j])
                    {
                    if(ls!=-1){
                        ans+=max(0,ls-(-nums[j]+target[j]));
                        if(ls>(-nums[j]+target[j]))
                        mv=min(mv,(-nums[j]+target[j]));
                    }
                    ls=-nums[j]+target[j];
                    j++;
                }
                            if(j>i)
                mv=-nums[j-1]+target[j-1],ans+=mv;

            }
            // cout<<ans<<endl;
        }
        return ans;
    }
};
