class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [0] * n
        for i in range(n):
            diff[i] = target[i] - nums[i]
        # 将diff中的数组全部变为0的操作数
        # 区间修改的最少操作次数
        # 对于当前值 
        # 要么直接用操作次数，要么变成与之后的数字一样， 
        pre = diff[0]
        ans = l = 0
        cnt = 0
        for r, x in enumerate(diff):
            if x >= 0:
                if pre < 0:
                    cnt = 0
                if x >= cnt:
                    ans += x - cnt
                cnt = x
            else:
                if pre > 0:
                    cnt = 0
                if x <= cnt:
                    ans += abs(x - cnt)
                cnt = x
            pre = x
        return ans
