class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int pv = 0;
        for (int i = 0, n = nums.size(); i < n; i++){
            int diff = target[i] - nums[i];
            int signp = pv >= 0;
            int signc = diff >= 0;
            if (pv >= 0 && diff >= 0){
                if (pv < diff){
                    ans += (diff - pv);
                }
            } 
            else if (pv < 0 && diff < 0){
                if (abs(pv) < abs(diff)){
                    ans += abs(diff) - abs(pv);
                }
            }
            else {
                ans += abs(diff);
            }
            pv = diff;
        }
        return ans;
    }
};
