class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        vector<long long> c(n);
        for(int i = 0; i < n; i ++ ){
            c[i] = -b[i] + a[i];
        }
        for(int i = n - 1; i; i -- ) c[i] -= c[i - 1];
        long long s1 = 0, s2 = 0;
        for(int i = 0; i < n; i ++ ){
            if(c[i] < 0) s1 -= c[i];
            else s2 += c[i];
        }
        if(s1 > s2) swap(s1, s2);
        return s1 + s2 - s1;
    }
};
