class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        nums=[j-i for i,j in zip(nums,target)]
        @cache
        def solve(ind,z,inc,dec):
            if ind==len(nums):
                return 0
            need=abs(nums[ind])
            if nums[ind]==0:
                z=0
                inc=0
                dec=0
            elif nums[ind]>0:
                need-=inc
                need=max(0,need)
                inc=abs(nums[ind])
                dec=0
                z=0
            else:
                need-=dec
                need=max(0,need)
                dec=abs(nums[ind])
                inc=0
                z=0
            return need+solve(ind+1,z,inc,dec)

        ans=solve(0,0,0,0)
        solve.cache_clear()
        return ans
