class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int[] arr = new int[nums.length];
        for(int i=0;i<nums.length;i++){
            arr[i] = target[i]-nums[i];
        }
        int[] prev = new int[nums.length];
        prev[0] = target[0]-nums[0];
        long ans = Math.abs(prev[0]);
        for(int i=1;i<nums.length;i++){
            if(arr[i]>0 && prev[i-1]>0){
                if(arr[i]>prev[i-1]){
                    ans += (long)(arr[i]-prev[i-1]);
                }
            }
            else if(arr[i]<0 && prev[i-1]<0){
                if(arr[i]<prev[i-1]){
                    ans += (long)(prev[i-1]-arr[i]);
                }
            }
            else{
                ans += (long)(Math.abs(arr[i]));
            }
            prev[i] = arr[i];
        }
        return ans;
    }
}
