class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0;
        int n = nums.size();
        vector<int> d{0};
        for(int i = 0; i < n; ++i) {
            d.push_back(target[i] - nums[i]);
        }
        for(int i = 1; i <= n; ++i) {
            int t = d[i] - d[i - 1];
            if(t > 0 && d[i] > 0) {
                res += d[i] - max(0, d[i - 1]);
            }
            if(t < 0 && d[i] < 0) {
                res += min(0, d[i - 1]) - d[i];
            }
        }
        return res;
    }
};
