class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff;
        for(int i = 0; i < nums.size(); i++) {
            diff.push_back(target[i] - nums[i]);
        }
        long long minOperation = abs(diff[0]);

        for(int i = 1; i < diff.size(); i++) {
            int lastDiff = diff[i-1];
            int currentDiff = diff[i];
            // if current is positive
            if(currentDiff >= 0) {
                if(lastDiff < currentDiff && lastDiff >= 0) {
                    minOperation += (currentDiff - lastDiff);
                }
                else if (lastDiff < 0) {
                    minOperation += abs(currentDiff);
                }
            }
            else {
                if(lastDiff > currentDiff && lastDiff < 0) {
                    minOperation += (lastDiff - currentDiff);
                }
                else if (lastDiff >= 0) {
                    minOperation += abs(currentDiff);
                }
            }
        }
        return minOperation;
    }
};
