class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        def minNumberOperations(target: List[int]) -> int:
            n = len(target)
            ans = target[0]
            for i in range(1, n):
                ans += max(target[i] - target[i - 1], 0)
            return ans
        l = [a - b for a, b in zip(target, nums)]
        n = len(l)
        left, right = 0, 0
        res = 0
        while right < n:
            if l[left] >= 0:
                while right < n and l[right] >= 0:
                    right += 1
                res += minNumberOperations(l[left : right])
                left = right
            else:
                while right < n and l[right] <= 0:
                    right += 1
                temp = [-num for num in l[left : right]]
                res += minNumberOperations(temp)
                left = right
        return res
