class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:


        diff = [n-t for n,t in zip(nums,target)]
        #print(diff)

        prev = 0
        #for v in diff:

        res = 0
        #1 -1
        for k in diff:

            if k > 0 and prev > 0:
                res += max(0, k - prev)
            elif k < 0 and prev < 0:
                res += max(0,  prev - k )
            else:
                res += abs(k)
            #print(res)
            prev = k
        return res

