class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        posi = [0]
        neg = [0]
        for x, y in zip(nums, target):
            if y >= x:
                posi.append(y-x)
                neg.append(0)
            else:
                posi.append(0)
                neg.append(x - y)

        posi.append(0)
        neg.append(0)
        n = len(posi)
        ans = 0


        def do(arr):
            nonlocal ans
            st = []
            for i in range(n):
                while len(st) > 1 and arr[st[-1]] > arr[i]:
                    ans += arr[st[-1]] - max(arr[st[-2]], arr[i])
                    st.pop()
                st.append(i)

        do(posi)
        do(neg)
        return ans
