class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long sum = 0;
        int pDelta = 0, lmin = 0;
        for(int i = 0; i < nums.size(); ++i){
            int delta = target[i] - nums[i]; // + if add, - if subtract
            if(!pDelta) sum += abs(delta);
            else if(delta && pDelta){
                if(pDelta < 0 && delta > 0) sum += delta;
                else if(pDelta > 0 && delta < 0) sum += abs(delta);
                else if(pDelta > 0 && delta > pDelta) sum += abs(delta - pDelta); // delta increase
                else if(pDelta < 0 && delta < pDelta) sum += abs(pDelta - delta); // delta increase
            }
            pDelta = delta;
        }
        return sum;
    }
};
