class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long ans=0;
        long long  inc=0;
        long long dec=0;
        for(int i=0;i<n;i++){
            if(nums[i]==target[i]){
                inc=0;
                dec=0;
            }
            else if(nums[i]<target[i]){
                dec=0;
                int dif=target[i]-nums[i];
                if(dif>inc){
                    ans+=dif-inc;
                    inc=dif*1LL;
                }
                inc=min(dif*1LL,inc);
            }
            else{
                inc=0;
                int dif=nums[i]-target[i];
                if(dif>dec){
                    ans+=dif-dec;
                    dec=dif*1LL;
                }
                dec=min(dec,dif*1LL);
            }
        }
        return ans;
    }
};
