class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [t - n for n, t in zip(nums, target)]
        # print(diff)
        ans = abs(diff[0])
        for a, b in pairwise(diff):
            if a ^ b < 0:
                ans += abs(b)
            elif (t := abs(b) - abs(a)) > 0:
                ans += t
            # print(ans)

        return ans
