class Solution {
public:
    long long minimumOperations(vector<int>& v, vector<int>& a) {
        int n=v.size();
        long long ans=0;
        int i=0;
        while(i<n){
            if(v[i]>a[i]){
                long long cnt=v[i]-a[i];
                ans+=cnt;
                i++;
                while(i<n and v[i]>a[i]){
                    long long temp=v[i]-a[i];
                    if(temp>cnt){
                        ans+=temp-cnt;
                        cnt=temp;
                    } else{
                        cnt=temp;
                    }
                    i++;
                }
            } else if(v[i]<a[i]){
                long long cnt=a[i]-v[i];
                ans+=cnt;
                i++;
                while(i<n and v[i]<a[i]){
                    long long temp=a[i]-v[i];
                    if(temp>cnt){
                        ans+=temp-cnt;
                        cnt=temp;
                    } else{
                        cnt=temp;
                    }
                    i++;
                }
            } else i++;
        }
        return ans;
    }
};
