class Solution {
public:
    long long func(vector<long long>&temp,int n){
        if(n==0) return 0;
        long long mini=*min_element(temp.begin(),temp.end());
        int i=0;
        long long ans=mini;
        while(i<n){
            vector<long long>temp1;
            while(i<n && temp[i]!=mini){
                temp1.push_back(temp[i]-mini);
                i++;
            }
            while(i<n && temp[i]==mini){
                i++;
            }
            int x=temp1.size();
            ans+=func(temp1,x);
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<long long>diff(n);
        for(int i=0; i<n; i++){
            diff[i]=nums[i]-target[i];
        }
        int i=0;
        long long ans=0;
        while(i<n){
            vector<long long>temp;
            if(diff[i]==0){
                i++;
                continue;
            }
            if(diff[i]>0){
                while(i<n && diff[i]>0){
                    temp.push_back(diff[i]);
                    i++;
                }
            }
            else{
                while(i<n && diff[i]<0){
                    temp.push_back(-1*diff[i]);
                    i++;
                }
            }
            int x=temp.size();
            ans+=func(temp,x);
        }
        return ans;
    }
};
