class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        dis = [t-n for n, t in zip(nums, target)]
        # print(dis)
        to_ret = 0
        history = 0
        for d in dis :
            dist = 0
            if history >= d >= 0 :
                pass
            elif d >= history >= 0 :
                dist = (d-history)
            elif 0 >= history >= d :
                dist = (history-d)
            elif 0 >= d >= history :
                pass
            else :
                dist = abs(d)
            to_ret += dist
            history = d
            # print(dist)
        return to_ret
