class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff=[target[i]-nums[i] for i in range(len(target))]
        segs=[]
        l=0
        for i in range(1, len(nums)):
            if(not ((diff[i] > 0) == (diff[l] > 0))):
                segs.append([l, i - 1])
                l = i
        segs.append([l, len(nums) - 1])
        ans=0
        def f(arr):
            arr=[abs(i) for i in arr]
            arr2=arr.copy()
            ops=0
            ops=arr[0]
            store=arr[0]
            for i in range(1, len(arr)):
                if(arr[i] >= store):
                    ops+=arr[i]-store
                    store=arr[i]
                else:
                    store=arr[i]
            print(arr2,ops)
            return ops

        for seg in segs:
            ans+=f(diff[seg[0]: seg[1] + 1])
        #print(segs,diff)
        return ans
