class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        a = []
        for x, y in zip(nums, target):
            a.append(y - x)
        ans = pre = 0
        for v in a:
            if v > 0:
                if v > pre:
                    ans += v - max(0, pre)
            elif v < 0:
                if v < pre:
                    ans += min(0, pre) - v
            pre = v
        return ans
