class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long result = 0;
        for(int i = 0; i < n; i++){
            if(nums[i] == target[i])
                continue;
            else if(nums[i] < target[i]){
                int temp = target[i] - nums[i];
                result += temp;
                nums[i] += temp;
                for(int j = i+1; j < n; j++){
                    if(nums[j] < target[j]){
                        temp = min(temp, target[j]-nums[j]);
                        nums[j] += temp;
                    }
                    else{
                        break;
                    }
                }
            }
            else if(nums[i] > target[i]){
                int temp = nums[i] - target[i];
                result += temp;
                nums[i] -= temp;
                for(int j = i+1; j < n; j++){
                    if(nums[j] > target[j]){
                        temp = min(temp, nums[j]-target[j]);
                        nums[j] -= temp;
                    }
                    else{
                        break;
                    }
                }
            }
        }
        return result;
    }
};
