class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        dif = [0]*len(nums)
        prev= 0
        cnt = 0
        for i in range(len(nums)):
            dif[i]=target[i]-nums[i]
            if(prev*dif[i]>0):# same sign
                if(abs(prev)>abs(dif[i])):pass
                else:cnt += abs(dif[i]-prev)
            elif(prev*dif[i]==0):
                cnt+=abs(dif[i])
            else:
                cnt += abs(dif[i])
            prev = dif[i]
            #print(cnt)
        return cnt
