int sign(int n){
    return (n > 0) - (n < 0);
}
int localmm(int b, int n, int a){
    return (n > b && n > a) - (n < b && n < a);
}
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> d(1, 0);
        for(int i=0; i<nums.size(); i++){
            int dd = target[i] - nums[i];
            if(*d.rbegin() != dd)
                d.push_back(dd);
        }
        d.push_back(0);
        int last = 0;
        long long ans = 0;
        for(int i=1; i<d.size()-1; i++){
            if(sign(d[i]) != sign(last)){
                ans += abs(d[i]);
                last = d[i];
            } else if (abs(d[i]) > abs(last)) {
                ans += abs(d[i] - last);
                last = d[i];
            } else {
                last = d[i];
            }
        }
        return ans;
    }
};
