class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = 0;
        int a[] = new int[nums.length];
        int n = nums.length;
        for(int i=0;i<n;i++){
            a[i] = target[i]-nums[i];
        }

       int maxpos = 0;
        int minpos =0;
        int minneg = 0;
        int maxneg = 0;
        for(int i=0;i<n;i++){
            // if(i==0){
            //     if(a[i]>0)maxpos = Math.max(maxpos, a[i]);
            //     else minneg = Math.min(minneg, a[i]);
            //     continue;
            // }
            if(a[i]==0){
                ans+=(maxpos-minpos);
                minpos = maxpos = a[i];
                ans+=(maxneg-minneg);
                minneg = maxneg = a[i];

            }
            if(a[i]>0){
                ans+=(maxneg-minneg);
                minneg = maxneg = 0;
                if(a[i]>=maxpos) maxpos = a[i];
                else{
                    ans+=(maxpos-minpos);
                    minpos = maxpos = a[i];
                }
            }
            if(a[i]<0){
                ans+=(maxpos-minpos);
                minpos = maxpos = 0;
                if(a[i]<=minneg)minneg = a[i];
                else{
                    ans+=(maxneg-minneg);
                    minneg = maxneg = a[i];
                }
            }

        }
        ans+=(maxpos-minpos);
        ans+=(maxneg-minneg);
        return ans;
    }
}
