class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans = 0;
        int lastMinAbs = 0;
        bool numsGreater = (nums[0] > target[0]);
        for (int i = 0; i < n; i++) {
            if ((nums[i] > target[i]) != numsGreater) {
                numsGreater = (nums[i] > target[i]);
                lastMinAbs = 0;
            }
            int absI = abs(nums[i] - target[i]);
            ans += max(absI - lastMinAbs, 0);
            lastMinAbs = absI;
        }
        return ans;
    }
};
