typedef long long LL;

class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        LL res = 0;
        int n = a.size();
        int gap[n + 10];
        memset(gap, 0, sizeof gap);

        for (int i = 0; i < n; i++) a[i] = a[i] - b[i];
        gap[0] = a[0];
        for (int i = 1; i < n; i++) gap[i] = a[i] - a[i - 1];
        gap[n] = 0-a[n - 1];

        for (int i = 0; i <= n; i++) {
            if (gap[i] > 0) res += gap[i];
        }

        return res;
    }
};
