#define ll long long


class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n=nums.size();
        ll diff[n];
        for(int i=0;i<n;i++){
            diff[i]=target[i]-nums[i];
        }
        vector<pair<ll,ll>> vp;
        ll ans=0;
        for(ll i=0;i<n;i++){
            ll vs=0;
            ll j=i;
            while(j<n&&diff[j]>0){
                j+=1;
            }
            if(i!=j) vp.push_back({i,j}) ,i=j-1;
        }

        for(auto [x,y]:vp){
            ll last=0;
            for(ll i=x;i<y;i++){
                if(diff[i]>last) ans+=(diff[i]-last);
                last=diff[i];
            }
        }
        vp.clear();
        for(int i=0;i<n;i++){
            ll vs=0;
            ll j=i;
            while(j<n&&diff[j]<0){
                j+=1;
            }
            if(i!=j) vp.push_back({i,j}) ,i=j-1;
        }
        for(auto [x,y]:vp){
            ll last=0;
            for(ll i=x;i<y;i++){
                if(diff[i]<last) ans+=abs(diff[i]-last);
                last=diff[i];
            }
        }
        return ans;
    }
};
