class Solution {
public:
    #define vi                          vector<int>
    #define vvi                         vector<vector<int>> 
    #define rep(i,a,b)                  for(int i = a; i < b; i++)
    #define repr(i,a,b)                 for(int i=a;i>=b;i--)
    #define endl                        "\n"
    #define ff                          first
    #define ss                          second
    #define cy                          cout << "YES" << endl
    #define cn                          cout << "NO" << endl
    #define si                          set<int>
    #define sc                          set<char>
    #define mii                         map<int,int> 
    #define mci                         map<char,int>
    #define pb                          push_back
    #define pp                          pop_back
    #define msi                         map<string, int>
    #define mis                         map<int, string>
    #define in                          insert
    #define M                           1000000007
    #define pi                          3.14159265358979323846
    #define prec(x)                     cout << fixed << setprecision(x)
    #define bpc(x)                      __builtin_popcountll(x)
    #define ull                         unsigned long long
    #define all(x)                      x.begin(), x.end()
    long long res(vector<int>& A)  
    {  
        for(auto &i : A) i=abs(i);
        long long ans = A[0];  
        for (int i = 1; i < A.size(); i++) {  

            ans += max(A[i] - A[i - 1], 0);  
        }  
        return ans;  
    } 
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n=a.size();
        vector<long long> c;
        long long ans2=0;
        rep(i,0,n){
            c.pb(a[i]-b[i]);
            ans2+=abs(a[i]-b[i]);
        }
        vector<int> x;
        long long ans=0;
        rep(i,0,n){
            if(x.size()==0&&c[i]!=0){
                x.pb(c[i]);
            }
            else if(c[i]==0){
                if(x.size()>0) ans+=res(x);
                x.clear();
            }
            else if(c[i]*x.back()<0){
                ans+=res(x);
                x.clear();
                x.pb(c[i]);
            }
            else{
                x.pb(c[i]);
            }
        }
        if(x.size()>0) ans+=res(x);
        return min(ans2,ans);
    }   
};
