class Solution {
public:
    #define ll long long int
    ll calc(vector<ll>&v){
        ll n=v.size();
        if(n==0)return 0;
        ll ans=v[0];
        for(ll i=1;i<n;i++){
            if(v[i] > v[i-1]){
                ans+=v[i]-v[i-1];
            }
        }
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n = nums.size();
        vector<ll>diff(n);

        for(int i=0;i<n;i++){
            diff[i] = target[i] - nums[i];
        }

        vector<vector<ll> >v;
        bool pos = true;

        vector<ll>curr;
        for(ll i=0;i<n;i++){
            if(diff[i]>=0){
                if(pos == true){
                    curr.push_back(diff[i]);
                }
                else{
                    if(curr.size() > 0)v.push_back(curr);
                    pos=true;
                    curr.clear();
                    curr.push_back(diff[i]);
                }
            }
            else if(diff[i]<=0){
                if(pos == false){
                    curr.push_back(-1*diff[i]);
                }
                else{
                    if(curr.size() > 0)v.push_back(curr);
                    pos=false;
                    curr.clear();
                    curr.push_back(-1*diff[i]);
                }
            }
        }

        if(curr.size() > 0)v.push_back(curr);

        ll ans=0;
        for(auto v1:v){
            // for(auto x:v1)cout<<x<<" ";
            // cout<<endl;
            ans+=calc(v1);
        }
        return ans;
    }
};
